import React from "react";
import "./CheckoutProduct.css";
import { useStateValue } from "./StateProvider";

function CheckoutProduct({ id, image, title, price, rating, hideButton }) {
  const [{ cart }, dispatch] = useStateValue();

  const removeFromBasket = () => {
    dispatch({
      type: "REMOVE_FROM_CART",
      id: id,
    });
  };
  return (
    <div className="my__product">
      <img src={image} alt="" />

      <div className="my__product__info">
        <p>{title}</p>
        <p className="my__product__price">
          <small>$</small>
          <strong>{price}</strong>
        </p>
        <div className="my__product__rating">
          {Array(rating)
            .fill()
            .map((_, i) => (
              <p>‚≠ê</p>
            ))}
        </div>
      </div>
      <div className="my__product__btn"></div>
      {!hideButton && <button className="" onClick={removeFromBasket}>Remove</button>}
    </div>
  );
}

export default CheckoutProduct;
