import React from 'react';
import "./Poster.css";

import poster_one from "./img/poster__home__page/poster_one.jpg";
import poster_two from "./img/poster__home__page/poster_two.jpg";
import poster_three from "./img/poster__home__page/poster_three.jpg";

function Poster() {

  const imgArr = [poster_one, poster_two, poster_three];
  const number = imgArr.map((image,index)=>{
    return (
    <button
            type="button"
            data-bs-target="#carouselExampleIndicators"
            data-bs-slide-to= {index++}
            className="active"
            aria-current="true"
            aria-label="Slide " 
      ></button>
      );
  })
  const carousel = imgArr.map(function (image, index) {
    if (index === 0) {
      return (
        <div className='carousel-item active'>
          <img src={image} className='d-block w-100' alt='...' />
        </div>
      );
    }

    return (
      <div className='carousel-item'>
        <img src={image} className='d-block w-100' alt='...' />
      </div>
    );
  });

  return (
    <div className="container">
      <div
        id="carouselExampleIndicators"
        className="carousel slide"
        data-bs-ride="carousel"
      >

    
        <div className="carousel-indicators">
          
          {number}
          
        </div>
        <div className="carousel-inner h-50">

       {carousel}
          
        </div>


        <button
          className="carousel-control-prev"
          type="button"
          data-bs-target="#carouselExampleIndicators"
          data-bs-slide="prev"
        >
          <span
            className="carousel-control-prev-icon"
            aria-hidden="true"
          ></span>
          <span className="visually-hidden">Previous</span>
        </button>
        <button
          className="carousel-control-next"
          type="button"
          data-bs-target="#carouselExampleIndicators"
          data-bs-slide="next"
        >
          <span
            className="carousel-control-next-icon"
            aria-hidden="true"
          ></span>
          <span className="visually-hidden">Next</span>
        </button>
      </div>
    </div>

  );
}
export default Poster;
