import React, { useState, useEffect } from "react";
import "./Home.css";
import Poster from "./Poster";
import poster_one from "./img/poster__home__page/poster_one.jpg";
import poster_two from "./img/poster__home__page/poster_two.jpg";
import poster_three from "./img/poster__home__page/poster_three.jpg";
import poster_four from "./img/poster__home__page/poster_four.jpg";
import Product from "./Product";
import product_one from "./img/product_img/product_one.jpg";
import product_two from "./img/product_img/product_two.jpg";
import product_three from "./img/product_img/product_three.jpg";

function Home() {
  const banners = [poster_one, poster_two, poster_three, poster_four];

  const [postList, setPostList] = useState([]);
  useEffect(() => {
    getPost()
    })
  const getPost = () => {
    const post = [
      {
        id: "4754",
        title:
          "Factory Supply Attractive Price Red Black Custom Mens Shirt Men's Casual Shirt",
        price: "245",
        image: { product_two },
        rating: "2",
      },
      {
        id: "4754",
        title:
          "Factory Supply Attractive Price Red Black Custom Mens Shirt Men's Casual Shirt",
        price: "245",
        image: { product_two },
        rating: "2",
      },
      {
        id: "4754",
        title:
          "Factory Supply Attractive Price Red Black Custom Mens Shirt Men's Casual Shirt",
        price: "245",
        image: { product_two },
        rating: "2",
      },
      {
        id: "4754",
        title:
          "Factory Supply Attractive Price Red Black Custom Mens Shirt Men's Casual Shirt",
        price: "245",
        image: { product_two },
        rating: "2",
      },
      {
        id: "4754",
        title:
          "Factory Supply Attractive Price Red Black Custom Mens Shirt Men's Casual Shirt",
        price: "245",
        image: "product_two",
        rating: "2",
      },
      {
        id: "4754",
        title:
          "Factory Supply Attractive Price Red Black Custom Mens Shirt Men's Casual Shirt",
        price: "245",
        image: "product_two",
        rating: "2",
      },
    ];
    setPostList(post);
  };
  

  return (
    <div className="home">
      <div className="container-fluid">
        <div className="banner">
          <Poster bannersArray={banners} />
        </div>
        <button onClick={getPost}>Click</button>
      

        <div className="row">
          {postList.map((item, index) => (
            <div className="col-sm-3">
              <Product
                id={item.id}
                title={item.title}
                price={item.price}
                image={product_one}
               
              />
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Home;
