import React from "react";
import "./Poster.css";

function Poster({ bannersArray }) {
  const number = bannersArray.map((image, index) => {
    if (index === 0) {
      return (
        <button
          type="button"
          data-bs-target="#carouselExampleIndicators"
          data-bs-slide-to={index}
          className="active"
          aria-current="true"
          aria-label="Slide 1"
        ></button>
      );
    } else {
      var slide = "Slide " + (index + 1);
      return (
        <button
          type="button"
          data-bs-target="#carouselExampleIndicators"
          data-bs-slide-to={index}
          aria-current="true"
          aria-label={slide}
        ></button>
      );
    }
  });

