import React, { useState, useEffect } from "react";
import "./ProductUpdate.css";
import DeleteForeverIcon from "@material-ui/icons/DeleteForever";

import upload from "../icon/upload.png";
function ProductUpdate() {
  const [selectedFile, setSelectedFile] = useState();
  const [preview, setPreview] = useState();
  const [productId, setProductId] = useState("");
  const [productName, setProductName] = useState("");
  const [productDescription, setProductDescription] = useState("");
  const [expireDate, setExpireDate] = useState("");
  const [unitStoks, setUnitStoks] = useState("");
  const [productPrice, setProductPrice] = useState("");
  const [imgURL, setImgURL] = useState("");


  const sendproduct= (e) => {
    e.preventDefault();
   const data = {

    productId: productId,
    productName: productName,
    productDescription: productDescription,
    expireDate: expireDate,
    unitStoks: unitStoks,
    productPrice: productPrice,
    imgURL: imgURL
   }
  
   axios.post('http://localhost:8080/api/feedback', data).then(res => {
     setData(res.data);
     setEmail('');
     setMessage('');
   }).catch(err => {
     console.log("error...")
   });
 }

  useEffect(() => {
    if (!selectedFile) {
      setPreview(undefined);
      return;
    }

    const objectUrl = URL.createObjectURL(selectedFile);
    setPreview(objectUrl);

    return () => URL.revokeObjectURL(objectUrl);
  }, [selectedFile]);

  const onSelectFile = (e) => {
    if (!e.target.files || e.target.files.length === 0) {
      setSelectedFile(undefined);
      return;
    }

    // I've kept this example simple by using the first image instead of multiple
    setSelectedFile(e.target.files[0]);
  };

  const [productList, setProductList] = useState([]);

  useEffect(() => {
    getProduct();
  });

  const getProduct = () => {
    const product = [
      {
        productId: "4754",
        productName: "Casual Shirt",
        price: "2557",
        instock: "245",
        expireDate: "25-5-23",
      },
      {
        productId: "564",
        productName: "Casual Pant",
        price: "4858",
        instock: "56",
        expireDate: "28-5-23",
      },
    ];
    setProductList(product);
  };
  return (
    <div>
      <main className="mt-5 pt-3">
        <div className="container-fluid mb-5">
          <div className="row">
            <div className="col-md-12 fw-bold fs-4">Add Product</div>
          </div>
          <div className="row">
            <div className="col-md-6 col">
              <form>
                <div className="form-group mt-2">
                  <label for="Input1">Product Id</label>
                  <input
                    type="text"
                    className="form-control mb-3"
                    id="Input1"
                    value={productId}
                    onChange={(e) => setProductId(e.target.value)}
                  />
                </div>
                <div className="form-group mt-2">
                  <label for="Input1">Product Name</label>
                  <input
                    type="email"
                    className="form-control mb-3"
                    id="Input1"
                    value={productName}
                    onChange={(e) => setProductName(e.target.value)}
                  />
                </div>
                <div className="form-group mt-2">
                  <label for="Input2 mr-2">Product Description</label>
                  <textarea
                    className="form-control"
                    aria-label="With textarea"
                    id="Input2"
                    value={productDescription}
                    onChange={(e) => setProductDescription(e.target.value)}
                  ></textarea>
                </div>
                <div className="form-group mt-2">
                  <div className="container">
                    <div className="row">
                      <div className="col-md-6 col-sm-12">
                        <label for="Input3">Expire Date</label>
                        <input
                          type="text"
                          className="form-control mb-3"
                          id="Input3"
                          value={expireDate}
                          onChange={(e) => setExpireDate(e.target.value)}
                        />
                      </div>
                      <div className="col-md-6 col-sm-12">
                        <label for="Input4">Units In Stock</label>
                        <input
                          type="text"
                          className="form-control"
                          id="Input4"
                          value={unitStoks}
                          onChange={(e) => setUnitStoks(e.target.value)}
                        />
                      </div>
                    </div>
                  </div>
                </div>
                <div className="form-group mt-2">
                  <label for="Input1">Product Price</label>
                  <input
                    type="number"
                    className="form-control mb-3"
                    id="Input1"
                    value={productPrice}
                    onChange={(e) => setProductPrice(e.target.value)}
                  />
                </div>
              </form>
            </div>
            <div className="col-md-6 col col-offset-6">
              <label for="files">
                <img src={upload} alt="" className="img-thumbnail" />
              </label>
              <input
                onChange={onSelectFile}
                type="file"
                id="files"
                value={imgURL}
                onChange={(e) => setImgURL(e.target.value)}
                hidden
              />
              {selectedFile && (
                <img
                  src={preview}
                  style={{ marginLeft: "15px", width: "300px" }}
                />
              )}
            </div>
          </div>
          <button className="btn btn-success">Add Product Now</button>
        </div>

        <div className="container-fluid">
          <div className="row">
            <div className="col-md-12 fw-bold fs-4">Product list</div>
          </div>
          <div className="row">
            <div className="col col-md-12">
              <table className="table">
                <thead>
                  <tr>
                    <th scope="col">PRODUCT__ID</th>
                    <th scope="col">PRODUCT NAME</th>
                    <th scope="col">PRICE</th>
                    <th scope="col">IN STOCK</th>
                    <th scope="col">EXPIRE DATE</th>
                    <th scope="col"></th>
                  </tr>
                </thead>
                <tbody>
                  {productList.map((product, index) => (
                    <tr>
                      <th scope="row">{product.productId}</th>
                      <td>{product.productName}</td>
                      <td>{product.price}</td>
                      <td>{product.instock}</td>
                      <td>{product.expireDate}</td>
                      <td>
                        <DeleteForeverIcon />
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
}

export default ProductUpdate;
