    {bannersArray.map((elem, i) => (
      <div className={"carousel-item " + (i === 0 ? "active" : "")}>
             <img src={elem} className="d-block w-100" alt="..." />
             </div>
           ))}
      
    </div>
  </div>
  <a className="carousel-control-prev" href="#carouselExampleControls" role="button" data-slide="prev">
