import React from 'react';
import "./Poster.css";

function Poster({image,position, name}) {
  const image4 = 'https://miro.medium.com/max/2800/0*JrIbdP7jK-8hNwny.jpg';
  const image5 = 'https://i.pinimg.com/originals/8e/2a/58/8e2a58243e9e5e6ed37928046aba64b8.jpg';
  const image6 = 'https://images.firstpost.com/wp-content/uploads/2019/04/assassins-creed.jpg';

  const imgArr = [image4, image5, image6];

  const carousel = imgArr.map(function (image, index) {
    if (index === 0) {
      return (
        <div className='carousel-item active'>
          <img src={image} className='d-block w-100' alt='...' />
        </div>
      );
    }

    return (
      <div className='carousel-item'>
        <img src={image} className='d-block w-100' alt='...' />
      </div>
    );
  });

  return (
    <div className="container">
      <div
        id="carouselExampleIndicators"
        className="carousel slide"
        data-bs-ride="carousel"
      >

    
        <div className="carousel-indicators">
          <button
            type="button"
            data-bs-target="#carouselExampleIndicators"
            data-bs-slide-to={position}
            className="active"
            aria-current="true"
            aria-label="Slide ${name}"
          ></button>
          
        </div>
        <div className="carousel-inner h-50">


          
        </div>


        <button
          className="carousel-control-prev"
          type="button"
          data-bs-target="#carouselExampleIndicators"
          data-bs-slide="prev"
        >
          <span
            className="carousel-control-prev-icon"
            aria-hidden="true"
          ></span>
          <span className="visually-hidden">Previous</span>
        </button>
        <button
          className="carousel-control-next"
          type="button"
          data-bs-target="#carouselExampleIndicators"
          data-bs-slide="next"
        >
          <span
            className="carousel-control-next-icon"
            aria-hidden="true"
          ></span>
          <span className="visually-hidden">Next</span>
        </button>
      </div>
    </div>

  );
}
export default Poster;
