import React, { useState, useEffect } from "react";
import "./Profile.css";
import img from "../img/admin/ok.jpg";
import { Link } from "react-router-dom";


function Profile() {
  const [profile, setProfile] = useState({});
  
  useEffect(() => {
    getProfile()
    })

  const getProfile = () => {
    const profile = 
      {
        name: "md. jakaria BP",
        email:"jonayed153@",
        password:"**************"
      }
      
    setProfile(profile);
  };
  return (
    <div>
      <main className="mt-5 pt-2">
        <div className="container-fluid">
          <div className="row">
            <div
              className="card cr shadow p-3 mb-5 bg-white rounded"
              style={{ width: "40rem" }}
            >
               
              <div className="card-body">
                  <img src={img} alt="" className="mb-4" style={{width:'180px'}}/>

                <p className="card-text font-weight-bold "> User Name</p>
                <p className="card-text "> Md. Jonayed Bepari</p>
                <p className="card-text font-weight-bold"> Email</p>
                <p className="card-text "> jonayed153@gmail.com</p>
                <p className="card-text font-weight-bold"> Password</p>
                <p className="card-text "> *************</p>
                <Link to="/editProfile" className="btn btn-warning px-5">
                Edit
                </Link>
              </div>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
}

export default Profile;
