import React, { useState, useEffect } from "react";
import "./Home.css";
import Poster from "./Poster";

import Product from "./Product";
import product_one from "./img/product_img/product_one.jpg";
import poster_four from "./img/poster__home__page/poster_four.jpg";
import axios from "axios";

function Home() {
  // const banners = [poster_one, poster_two, poster_three, poster_four];

  const [postList, setPostList] = useState([]);
  
  useEffect(() => {
    getPost();
    })
    const getPost = () => {
      axios.get("http://localhost:8080/api/product").then((res) => {
        setPostList(res.data);
      });
    };


  return (
    <div className="home">
      <div className="container-fluid">
        <div className="banner">
          <Poster product={poster_four}/>
        </div>
       
      

        <div className="row">
          {postList.map((item, index) => (
            <div className="col-sm-3">
              <Product
                id={item.id}
                title={item.productName}
                description={item.productDescription}
                price={item.productPrice}
                image={product_one}
              />
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Home;
