import React, { useState ,useEffect  } from "react";
import "./Home.css";
import Poster from "./Poster";
import poster_one from "./img/poster__home__page/poster_one.jpg";
import poster_two from "./img/poster__home__page/poster_two.jpg";
import poster_three from "./img/poster__home__page/poster_three.jpg";
import poster_four from "./img/poster__home__page/poster_four.jpg";
import Product from "./Product";
import product_one from "./img/product_img/product_one.jpg";
import product_two from "./img/product_img/product_two.jpg";
import product_three from "./img/product_img/product_three.jpg";

function Home() {
  const banners = [poster_one, poster_two, poster_three, poster_four];
  
  const [postList, setPostList] = useState([]);


  const getPost = ()=>{
    const post = [
      {
        title: "chun chun chun"
      }
    ];
    setPostList(post);
  };

  return (
    <div className="home">
      <div className="container-fluid">
        <div className="banner">
          <Poster bannersArray={banners} />
        </div>

        <div className="row">
          <button onClick={getPost()}>Click</button>
          {postList.map((item, index) => (
            <div className="col-sm-3">
              <Product
               id="4754"
               title={item.title}
               price={245}
               image={product_two}
               rating={2}
            />
            </div>
          ))}

          <div className="col-sm-3">
            <Product
              id="4754"
              title="Shop Laptops & Tablets"
              price={245}
              image={product_two}
              rating={2}
            />
          </div>
          <div className="col-sm-3">
            <Product
              id="4754"
              title="Shop Laptops & Tablets"
              price={245}
              image={product_three}
              rating={2}
            />
          </div>

          <div className="col-sm-3">
            <Product
              id="4754"
              title="Shop Laptops & Tablets"
              price={245}
              image={product_one}
              rating={2}
            />
          </div>
        </div>
        <div className="row">
          <div className="col-sm-12">
            <Product
              id="4427"
              title="Comfy styles for her"
              price={99}
              image={product_one}
              rating={5}
            />
          </div>
        </div>
        <div className="row">
          <div className="col-sm-4">
            <Product
              id="4427"
              title="Comfy styles for her"
              price={99}
              image={product_one}
              rating={5}
            />
          </div>
          <div className="col-sm-4">
            <Product
              id="4754"
              title="Shop Laptops & Tablets"
              price={245}
              image={product_two}
              rating={2}
            />
          </div>
          <div className="col-sm-4">
            <Product
              id="4754"
              title="Shop Laptops & Tablets"
              price={245}
              image={product_three}
              rating={2}
            />
          </div>
        </div>

        <div className="row">
          <div className="col-sm-6">
            <Product
              id="4427"
              title="Comfy styles for her"
              price={99}
              image={product_one}
              rating={5}
            />
          </div>
          <div className="col-sm-6">
            <Product
              id="4754"
              title="Shop Laptops & Tablets"
              price={245}
              image={product_two}
              rating={2}
            />
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
