import React from "react";
import "./Poster.css";

function Poster({bannersArray}) {
 
  const number = bannersArray.map((image, index) => {
   
      if(index==0){
        return (
          <button
        type="button"
        data-bs-target="#carouselExampleIndicators"
        data-bs-slide-to={index}
        className="active"
        aria-current="true"
        aria-label="Slide 1"
      ></button>
    );
      }else{
        var slide = "Slide "+(index+1);
        return (
          <button
        type="button"
        data-bs-target="#carouselExampleIndicators"
        data-bs-slide-to={index}
        aria-current="true"
        aria-label= {slide}
      ></button>
    );
      }
      
  });

  return (
    <div className="container">
      <div
        id="carouselExampleIndicators"
        className="carousel slide"
        data-bs-ride="carousel"
      >
        <div className="carousel-indicators">{number}</div>

        <div className="carousel-inner h-50">
          {bannersArray.map((elem, i) => (
            <div className={"carousel-item " + (i === 0 ? "active" : "")}>
              <img src={elem} className="check d-block w-100" alt="..." />
            </div>
          ))}
        </div>

        <button
          className="carousel-control-prev"
          type="button"
          data-bs-target="#carouselExampleIndicators"
          data-bs-slide="prev"
        >
          <span
            className="carousel-control-prev-icon"
            aria-hidden="true"
          ></span>
          <span className="visually-hidden">Previous</span>
        </button>
        <button
          className="carousel-control-next"
          type="button"
          data-bs-target="#carouselExampleIndicators"
          data-bs-slide="next"
        >
          <span
            className="carousel-control-next-icon"
            aria-hidden="true"
          ></span>
          <span className="visually-hidden">Next</span>
        </button>
      </div>
    </div>
  );
}
export default Poster;
